ruleToc.chapterList:
@js: var result = []
if (!baseUrl.includes('index')) {
    var chnList = java.getStringList("ul[class=ph_list].1@a@text");
    var chfList = java.getStringList("ul[class=ph_list].1@a@href");
} else {
    var chnList = java.getStringList(".ph_list a@text");
    var chfList = java.getStringList(".ph_list a@onclick");
}

for (var i = 0; i < chfList.length; i++) {
	chfList[i] = String(chfList[i]).replace(/read_tz\((\d+)\)\;/g,book.bookUrl + "$1.html");
	var chn = Array.from(chnList);
 var chf = Array.from(chfList);
    result.push({
        title: chn[i],
        url: chf[i]
    });
}

if (result.length === 0) {
    // 如果目录数据为空，则弹窗提示
    java.longToast("当前书籍无数据");
} else {
    result = result;
}
result;

