function getBaseUrl() {
	let BaseUrl = "https://bmkk.fun/api";
	try {
    BaseUrl = JSON.parse(java.get("https://update.bmkk.fun/update/update.php", {"User-Agent": "okhttp/4.10.0","n-application-type": "android"}).body()).StartApiDomain;
    if (!BaseUrl || BaseUrl.trim() == '') throw new Error('网络接口为空');
    java.toast(`api接口获取成功\n${BaseUrl}`);
    } catch (e) {
    	java.log(e.message);
    	java.toast('\n网络api接口获取失败，已设置为默认接口\nhttps://bmkk.fun/api');
    	}
    source.setVariable(BaseUrl);
}
if (source.getVariable() == '') getBaseUrl();

