function getval(kk) {
	   const { java } = this
    var val = "";
    for (var i = 0; i <= kk.length - 1; i++) {
        var str = kk.charAt(i);
        var code = str.charCodeAt();
        val += parseInt(code) + 1;
    }
    valu = java.md5Encode(val);
    return valu;
