var sgv = JSON.parse(source.getVariable());
var sg = source.get("list");
var ul = GetUL();

function reg() {
    java.startBrowser(`${ul}/user/user/reg.html`, "注册账号");
}

function Updatelog() {
    let cont = String(source.variableComment);
    let log = java.base64Decode(cont);
    java.longToast("\n" + log);
}

function login() {}

function Tgch(o) {
    let sort = ["默认", "更新时间", "人气", "字数"]
    sgv.s = o;
    source.setVariable(JSON.stringify(sgv, null, "\t"));
    java.toast(`\n已切换发现排序方式为【${sort[o]}】\n刷新发现即可生效`);
}

function Geturls() {
    let sgv1 = sgv.urls;
    sgv1.length = 0
    sgv1.push("https://alicesw.org");
    try {
        let res = org.jsoup.Jsoup.parse(java.ajax('https://alicesw.click/')).select('.button-box:nth-of-type(2)').attr('href');
        let src = String(java.ajax(res));
        let ul = org.jsoup.Jsoup.parse(src).select('a').attr('href');
        if(String(ul)){sgv1.push(ul)}
        //数组去重
        const seen = new Set();
        let uli = src.matchAll(/url=(https[^"]+)\/?\"/g);
        for (let match of uli) {
            const url = match[1];
            if (!seen.has(url)) {
                sgv1.push(url);
                seen.add(url);
            }
        }
        source.setVariable(JSON.stringify(sgv, null, "\t"));
        if (sgv1.length >= 2) {
            java.toast(`获取成功，共获取到${sgv1.length}个接口\n┎┅┅┅┅┅┅┅┅┅┅┅┅┒\n${sgv1.join('\n')}\n┖┅┅┅┅┅┅┅┅┅┅┅┅┚`);
        } else {
            java.toast('获取失败，请重试或检查网络状况');
        }
    } catch (e) {
        java.log(e);
        java.toast('未知错误，请使用最新beta测试版');
    }
}

function Puturl(e) {
    if (sgv.urls.length > 0 && e + 1 <= sgv.urls.length) {
        sgv.sourl = sgv.urls[e];
        source.setVariable(JSON.stringify(sgv, null, "\t"));
        //切换接口后重新设置cookie，保持登录状态
        let Cookie = cookie.getCookie(sgv.urls[e]);
        if (!/lf_user_auth/.test(Cookie) && source.getLoginHeader()) {
            let ck = source.getLoginHeaderMap()['Cookie']
            cookie.setCookie(sgv.urls[e], ck);
        }
        let stime = new Date().getTime();
        let sr = java.ajax(sgv.urls[e]);
        let etime = new Date().getTime();
        let speedtime = (etime - stime) / 1000;
        let point = sr.includes('Connection reset') ? "\n当前站源失效，请切换其他接口或检查网络状况" : `已设置接口为：\n${sgv.urls[e]}\n♦解析用时${speedtime}秒♦`;
        java.toast(point);
    } else if (sgv.urls.length === 0) {
        java.toast('\n当前没有接口，请获取后选择');
    } else if (e + 1 > sgv.urls.length) {
        java.toast(`暂无第${e+1}接口`);
    }
}

function Login() {
    cookie.removeCookie(ul);
    const loginul = `${ul}/user/user/login.html`;
    const codeul = `${ul}/user/user/verify.html`;

    const na = result['用户名/邮箱'];
    const pa = result['密码']
    if (String(na && pa)) {
        let phpid = java.get(codeul, {}).header("set-cookies");
        java.log(phpid);
        let code = java.getVerificationCode(codeul);
        let body = `username=${na}&password=${pa}&code=${code}`
        let cook = java.post(loginul, body, {
            "x-requested-with": "XMLHttpRequest",
            "Cookie": phpid
        });
        let ck = String(cook.cookies()).replace(/[{}]/g, "").replace(",", "; ");
        //java.log(String(ck))
        let headers = JSON.stringify({
            "Cookie": ck
        });
        let usercode = JSON.parse(cook.body()).msg
        java.toast(usercode);
        if (ck) {
            source.putLoginHeader(headers);
            cookie.replaceCookie(GetUL(), ck);
        } else {
            source.removeLoginHeader();
            cookie.removeCookie(GetUL());
        }
    }
}

function out() {
    source.removeLoginHeader();
    cookie.removeCookie(GetUL());
    java.toast("已退出登录状态");
}

function show() {
    let jc = source.getLoginHeaderMap();
    let us, id, codes
    if (jc) {
        let inf = decodeURIComponent(jc.get("Cookie"));
        let info = decodeURIComponent(inf).match(/\{[\s\S]+\}/)[0];
        //java.log(info);
        us = JSON.parse(info)['username'];
        id = JSON.parse(info)['uid'];
        codes = "✔️已登录";
    } else {
        us = "无";
        id = "无";
        codes = "❌未登录";
    }
    let txt = `\n◎用户：${us}◎\n◎用户ID：${id}◎\n◎登录状态：${codes}◎`
    java.longToast(txt);
}

function update() {
    sourcetime = String(source.lastUpdateTime);
    const upul = "https://fastly.jsdelivr.net/gh/yezechuandl/source-legado@main/source/爱丽丝书屋.json"
    let cc = java.ajax(upul)
    let uptime = String(JSON.parse(cc)[0].lastUpdateTime)
    if (Number(uptime) > Number(sourcetime)) {
        java.toast("检测到更新，正在跳转……");
        Packages.java.lang.Thread.sleep(2000);
        java.startBrowser('data:text/html;base64,' + nk, '更新');
    } else {
        java.toast(`本地书源版本较新，无需更新。\n本地书源编辑时间：${timeov(Number(sourcetime))}\n网络书源更新时间：${timeov(Number(uptime))}`);
    }
}

function AFBook(ta) {
    let info = result['书籍ID']
    if (String(info) && source.getLoginHeaderMap()) {
        let bookid = info
        java.log(bookid);
        try {
            let url
            if (ta) {
                url = `${ul}/user/bookshelf/add?novel_id=${bookid}`
            } else {

                let src = ""
                for (page = 1;; page++) {
                    let sr = java.ajax(`${ul}/user/bookshelf?page=${page}`);
                    if (!/书架空空如也/.test(sr)) {
                        src += sr + "\n";
                    } else {
                        break;
                    }
                }
                //java.log("书籍在书架第"+page+"页");
                let booklist = src.match(/\/novel\/\d+\.html/g);
                let dfid = src.match(/\/user\/bookshelf\/del[^"]+/g);
                for (i in booklist) {
                    if (booklist[i].includes(bookid)) {
                        ra = dfid[i];
                        break;
                    }
                }
                url = GetUL() + ra;
            }
            let msg = java.get(url, {
                "x-requested-with": "XMLHttpRequest"
            });
            let point = JSON.parse(msg.body()).msg
            java.toast(point);
        } catch (error) {
            //java.log(error);
            java.toast('无效操作');
        }
    } else if (!String(info)) {
        java.toast("\n未填入书籍id，请进书籍详情页，或者手动填写。");
    } else if (!source.getLoginHeaderMap()) {
        java.toast("未登录，请登录后使用");
    }
}

function BookShelf() {
    if (source.getLoginHeaderMap()) {
        java.startBrowser(`${ul}/user/bookshelf/`, "书架");
    } else {
        java.toast("未登录，请登录后再使用此功能。");
    }
}

function inBookShelf() {
    htm = booklist();
    java.startBrowser("data:text/html;base64," + htm, "书架");
}
//放置书籍信息至书源变量
function bookinto() {
    if (String(sg)) {
        let sgv1 = sgv.booklist;
        let sg1 = JSON.parse(sg);
        let exists = sgv1.some(item => {
            return Number(item.ID) === Number(sg1.ID);
        });
        if (!exists) {
            sgv1.push(sg1);
            source.setVariable(JSON.stringify(sgv, null, "\t"));
            java.toast("添加成功");
        } else {
            java.toast("书籍已存入本地书架中");
        }
    } else {
        java.toast('🚫请进入书籍详情页后操作，或者刷新试试')
    }
}

function bookmove(a) {
    try {
        let sgv1 = sgv.booklist;
        let sg1 = JSON.parse(sg);
        if (a == "all") {
            source.put("list", '')
            sgv1.length = 0
            source.setVariable(JSON.stringify(sgv, null, "\t"));
            java.toast('已清空本地书架');
        } else {
            let index = sgv1.findIndex(item => Number(item.ID) === Number(sg1.ID));
            if (index != -1) {
                sgv1.splice(index, 1);
                source.setVariable(JSON.stringify(sgv, null, "\t"));
                java.toast(`已从本地删除该书籍。\n书籍ID：${sg1.ID}`);
            } else {
                java.toast('书籍不在本地书架中')
            }
        }
    } catch (err) {
        java.log("错误日志" + err);
        java.toast('🚫请进入书籍详情页后操作，或者刷新试试');
    }
