function decrypt(encryptedTxt, keySeed) {
    const { java } = this;
    let result;
    try {
        const fullKey = java.md5Encode(keySeed);
        const ivBytes = java.strToBytes(fullKey.substring(0, 16));
        const keyBytes = java.strToBytes(fullKey.substring(16));

        const Cipher = java.createSymmetricCrypto(
            "AES/CBC/Pkcs7Padding",
            keyBytes,
            ivBytes
        );

        result = Cipher.decryptStr(java.base64DecodeToByteArray(encryptedTxt));
    } catch (e) {
        java.log(e.message);
        result = "解密失败";
    }
    return result;
}

