jsLib:
function openUrl(encryptedBase64, num) {
	  const { java , source } = this
        var numStr = java.base64Decode(num);
        var userAgent = String(source.header) ? JSON.parse(source.header)["User-Agent"].toLowerCase() : "mozilla/5.0 (windows nt 10.0; win64; x64) applewebkit/537.36 (khtml, like gecko) chrome/119.0.0.0 safari/537.36";
        var keyMd5 = java.md5Encode(userAgent + numStr);
        var key = java.strToBytes(keyMd5);
        var encryptedData = java.base64DecodeToByteArray(encryptedBase64);
         var iv = encryptedData.slice(0,16);
	       var Data = encryptedData.slice(16);
         var cipher = java.createSymmetricCrypto(
            "AES/CBC/PKCS5Padding", 
            key, 
            iv
        );
        return cipher.decryptStr(Data);
}

