function wn(t) {
                return "object" !== typeof t || null === t ? t :
                    Array.isArray(t) ? t.map(wn) : Object.keys(t)
                    .sort().reduce(((e, n) => (e[n] = wn(t[n]),
                    e)), {})
}

function mn(u, i, b) {
	 const { java , source } = this
	 let result;
	 if (source.getLoginHeader()) {
  const e = source.getLoginHeaderMap().get("Authorization");
  const n = Date.now();
  const base64String = java.base64Encode(n + u + e);
  const r = java.md5Encode(base64String);
	 const l = java.toURL(u).searchParams ? wn(Object.fromEntries(Array.from(java.toURL(u).searchParams).map(([k, v]) => [k, String(v)]))) : {};
  const s = java.toURL(u).pathname;
  const g = b ? wn(b) : {};
  const h = [i,s,JSON.stringify(l),JSON.stringify(g),n,r].join("|");
  //java.log(h);
  let key = java.HMacHex(h, "HmacSHA256", e);
  result = String(`${e};${key};${n};${r}`);
 } else {
 	 java.toast('未登录');
 	 result = "";
 	}
 return result;
}

//Authorization头过期校验（发现专用）
function Login_check() {
	  const { java, source, cookie } = this
	  var login_checkUrl = source.key + "/api/auth/isLogin";
	  if (source.getLoginHeader() && java.connect(login_checkUrl + "," + JSON.stringify({"headers":{
        "Authorization": mn.call(this, login_checkUrl, "GET")
    }})).code() >= 400) {
    eval(String(source.loginUrl));
    Login();
    }
return true;
}

function formatDate(dateStr) {
  const date = new Date(dateStr);
  const pad = n => String(n).padStart(2, '0');
  return `${date.getFullYear()}-${pad(date.getMonth()+1)}-${pad(date.getDate())} ` +
         `${pad(date.getHours())}:${pad(date.getMinutes())}:${pad(date.getSeconds())}`;
}

function decryptData(Data) {
	      const { java, source } = this 
    try {
    	   const userkey = source.getLoginHeaderMap().get("Authorization");
        const fullData = java.base64DecodeToByteArray(Data);
        const ivBytes = fullData.slice(0, 16);
        const ContentBytes = fullData.slice(16);

        const keyTime = 60000 * Math.floor(Date.now() / 60000);
        const keyTimes = [keyTime, keyTime - 60000, keyTime - 60000*2];
        //java.log(keyTimes.toString());

        for (let ks of keyTimes) {
            try {
                let keyHex = java.digestHex(userkey + ks, "SHA-256");
                let keyBytes = java.hexDecodeToByteArray(keyHex);

                let cipher = java.createSymmetricCrypto(
                    "AES/CBC/PKCS7Padding",
                    keyBytes,
                    ivBytes
                );

                let content = cipher.decryptStr(ContentBytes);
                //java.log(content);
                if (content) {
                	return String(content);
                	}
            } catch(e) {
                java.log(`使用【${ks}】拼接密匙，解密失败\n` +  e);
            }
        }
        throw new Error("所有密钥失效");
    } catch(e) {
        throw new Error(`解密过程出错: \n${e}`);
    }
}

var javaImport = new JavaImporter();
javaImport.importPackage(
    Packages.okhttp3
);
//构建Put请求函数;
function OkhttpPut(url, headers) {
        const { java } = this
        with(javaImport) {
        var body = RequestBody.create(null, "");

        var requestBuilder = new Request.Builder()
            .url(url)
            .put(body);

        if (headers) {
            for (let key in headers) {
                requestBuilder.addHeader(key, headers[key]);
            }
        }

        //java.log("PUT: " + url + ',{"headers":' + JSON.stringify(headers) + "}");

        let client = new OkHttpClient();
        let response = client.newCall(requestBuilder.build()).execute();
        let ResponseBody = response.body().string();
        if (ResponseBody.includes('Invalid signature')) {
        throw new Error("请求Authorization头过期，请检验登录状态");
        }
        return JSON.parse(ResponseBody);
    }
}

