function login() {
    let Str = source.getLoginInfoMap()?.get("🏷️展示最大数目（默认36，用|分隔）") ?? "";
    let original = JSON.parse(source.getVariable());
    if (/^\d+\|\d+$/.test(Str) || Str == "全部") {
        original.tagNumber = Str;
    } else {
        original.tagNumber = "36|36";
    }
    source.setVariable(JSON.stringify(original));
}

function Login(c) {
    cookie.removeCookie(source.key);
    var name = source.getLoginInfoMap()?.get("用户名") ?? "";
    var word = source.getLoginInfoMap()?.get("密码") ?? "";
    var invitationCode = source.getLoginInfoMap()?.get("邀请码") ?? "";
    let so = source.key;
    if (String(name) !== "" && String(word) !== "") {
        let url = so + "/api/auth/login";
        let body = {
            "email": name,
            "password": word,
            "invitationCode": invitationCode
        };
        let parame = JSON.stringify({
            "body": JSON.stringify(body),
            "method": "POST"
        });
        let Data = java.ajax(url + "," + parame);
        let UserData = {
            "Authorization": Data
        };
        if (/^\w.*-/.test(Data)) {
            let message = c ? `\n用户${name}\n登录成功` : "\n登录掉啦，已重获登录头";
            java.toast(message);
            source.putLoginHeader(JSON.stringify(UserData));
        } else {
            java.toast(Data);
        }
    } else {
        java.toast("请填入必要数据后点击右上角√");
    }
}

function bookUpdate() {
    if (!source.getLoginHeader() && Login_check()) return java.toast('未登录');
